            background-position: center;
            background-size: cover;
        }
        .hero-content{
            text-align: center;
            padding: 25px;
        }
        .form-div{
            width: 60%;
            margin: auto;
        } 
        .form-elements{
            width: 100%;
            height: 30px;
            padding: 5px;
            margin: 10px 0;
        }
        .submit-btn{
            background-color: #26a69a;
            color: #fff;
            border: none;
            padding: 10px;
            cursor: pointer;
            text-align: center;
        }
        .campaign-progress,
        .success-stories{
            margin: 25px auto;
            width: 80%;
            padding: 20px;
            border: 1px solid #26a69a;
        }
